"use strict";
const command_1 = require("@oclif/command");
const AdmZip = require("adm-zip");
class ZipCli extends command_1.Command {
    async run() {
        const { flags } = this.parse(ZipCli);
        const { input, output } = flags;
        const zip = new AdmZip();
        zip.addLocalFolder(input);
        zip.writeZip(output);
    }
}
ZipCli.description = 'zip-cli';
ZipCli.flags = {
    version: command_1.flags.version({ char: 'v' }),
    help: command_1.flags.help({ char: 'h' }),
    input: command_1.flags.string({ char: 'i', required: true }),
    output: command_1.flags.string({ char: 'o', required: true }),
};
module.exports = ZipCli;
